void Ili9341_Cmd(uint8_t a_Byte)
{
	LCD_SELECT;										// Enable Spi Chip Select
	LCD_CMD;										// Set Command
	LCD_TXBUFF(&a_Byte, 1);
//	while(LCD_BUSY == 1);							// Wait for Terminate of Transfer
	LCD_UNSELECT;									// Disable Spi Chip Select
}

void Ili9341_Data(uint8_t a_Byte)
{
	LCD_SELECT;										// Enable Spi Chip
	LCD_DATA;										// Set Data
	LCD_TXBUFF(&a_Byte, 1);
//	while(LCD_BUSY == 1);							// Wait for Terminate of Transfer
	LCD_UNSELECT;									// Disable Spi Chip Select
}

void Ili9341_Display(uint8_t a_Byte)
{
	a_Byte ? (Ili9341_Cmd(0x29)) : (Ili9341_Cmd(0x28));
	a_Byte ? (LCD_LEDON) : (LCD_LEDOFF);
}

void Ili9341_Reset(void)
{
	// Hardware Reset(GPIO)
	LCD_LEDON;				// Back Light is Turned On

//	LCD_RESET;				// Change to Low Level(Reset Active)
//	DWT_Delay_us(5);		// Delay Shorter than 5 us
//	LCD_SET;				// Change to High Level(Reset Inactive)
//	DWT_Delay_us(20);		// Delay(About 20 us)
//	LCD_RESET;				// Change to Low Level(Reset Active)
//	DWT_Delay_us(10);		// Delay Pulse Duration is Minimum 10 us
	LCD_SET;				// Change to High Level(Reset Inactive)
	HAL_Delay(120);			// Delay Reset Cancel Parameter is Minimum 120 ms
}

