void Ili9341_Rotate(uint16_t a_Angle)
{
	Ili9341_Cmd(0x36);

	switch(a_Angle)
	{
		case 0 :	// Vertical(X240 x Y320)
			Ili9341_Data(0x48);
			LcdHoriPixel = 240;
			LcdVertPixel = 320;
			break;
		case 90 :	// Horizontal(X320 x Y240)
			Ili9341_Data(0x28);
			LcdHoriPixel = 320;
			LcdVertPixel = 240;
			break;
		case 180 :	// Vertical(X240 x Y320)
			Ili9341_Data(0x88);
			LcdHoriPixel = 240;
			LcdVertPixel = 320;
			break;
		case 270 :	// Horizontal(X320 x Y240)
			Ili9341_Data(0xe8);
			LcdHoriPixel = 320;
			LcdVertPixel = 240;
			break;
		default :
			break;
	}
}

void Ili9341_Background(uint16_t a_Color)
{
	uint8_t l_Color[2] = {(uint8_t)(a_Color >> 8), (uint8_t)(a_Color & 0xff)};
	volatile uint32_t l_HoriCnt = 0, l_VertCnt = 0;

	Ili9341_Cmd(0x2a);		// Command Column Address Set
	Ili9341_Data(0x00);
	Ili9341_Data(0x00);
	Ili9341_Data((LcdHoriPixel-1) >> 8);
	Ili9341_Data((LcdHoriPixel-1) & 0xff);

	Ili9341_Cmd(0x2b);		// Command Page Address Set
	Ili9341_Data(0x00);
	Ili9341_Data(0x00);
	Ili9341_Data((LcdVertPixel-1) >> 8);
	Ili9341_Data((LcdVertPixel-1) & 0xff);

	Ili9341_Cmd(0x2c);		// Command Memory Write

	LCD_SELECT;				// Enable Spi4 Chip Select
	LCD_DATA;				// Set Data(1) or Command(0)
	for(l_VertCnt = 0; l_VertCnt < LcdVertPixel; l_VertCnt++)
	{
		for(l_HoriCnt = 0; l_HoriCnt < LcdHoriPixel; l_HoriCnt++)
		{
			LCD_TXBUFF(l_Color, 2);		// Transmit Spi4 Data
//			while(LCD_BUSY == 1);			// Wait for Terminate of Transfer
		}
	}
	LCD_UNSELECT;				// Disable Spi4 Chip Select
}

