				if(a_FontSize > (l_EndY-l_StartY+1))
				{
					break;
				}

				Ili9341_Cmd(0x2a);		// Command Column Address Set
				Ili9341_Data(l_StartX >> 8);
				Ili9341_Data(l_StartX & 0xff);
				Ili9341_Data((l_StartX+(a_FontSize >> 1)-1) >> 8);
				Ili9341_Data((l_StartX+(a_FontSize >> 1)-1) & 0xff);

