void Ili9341_Point(uint16_t a_Color, uint16_t a_Size, int16_t a_OriginX, int16_t a_OriginY)
{
	uint8_t l_PixelCnt = 0;
	uint8_t l_Color[2] = {(a_Color >> 8), (a_Color & 0xff)};
	uint8_t l_CoordX[4] = {(a_OriginX >> 8), (a_OriginX & 0xff), ((a_OriginX+a_Size-1) >> 8), ((a_OriginX+a_Size-1) & 0xff)};
	uint8_t l_CoordY[4] = {(a_OriginY >> 8), (a_OriginY & 0xff), ((a_OriginY+a_Size-1) >> 8), ((a_OriginY+a_Size-1) & 0xff)};
	uint16_t l_Size = a_Size;

	if(((a_OriginX+l_Size) > LcdHoriPixel) || ((a_OriginY+l_Size) > LcdVertPixel))
	{

	}

	Ili9341_Cmd(0x2a);		// Command Column Address Set
	LCD_SELECT;				// Enable Spi4 Chip Select
	LCD_DATA;				// Set Data(1) or Command(0)
	LCD_TXBUFF(l_CoordX, 4);
	LCD_UNSELECT;				// Enable Spi4 Chip Select

	Ili9341_Cmd(0x2b);		// Command Page Address Set
	LCD_SELECT;				// Enable Spi4 Chip Select
	LCD_DATA;				// Set Data(1) or Command(0)
	LCD_TXBUFF(l_CoordY, 4);
	LCD_UNSELECT;				// Enable Spi4 Chip Select

	Ili9341_Cmd(0x2c);		// Command Memory Write
	LCD_SELECT;				// Enable Spi4 Chip Select
	LCD_DATA;				// Set Data(1) or Command(0)
	for(l_PixelCnt = 0; l_PixelCnt<(a_Size*a_Size); l_PixelCnt++)
	{
		LCD_TXBUFF(l_Color, 2);
	}
	LCD_UNSELECT;				// Enable Spi4 Chip Select
}

