void Ili9341_LineDiag(uint16_t a_Color,
		int16_t a_OriginX, int16_t a_OriginY, int16_t a_OffsetX, int16_t a_OffsetY)
{
	int16_t l_Err = 0, l_EndX = 0, l_StepY = 1;
	uint16_t l_SwapFlg = 0;

	if(ABS(a_OffsetX) < ABS(a_OffsetY))	// Only Use Offset of X > Offset of Y(else, Swap Axis)
	{
		l_SwapFlg = 1;
		SWAPINT16(a_OriginX, a_OriginY);
		SWAPINT16(a_OffsetX, a_OffsetY);
	}
	if(a_OffsetX < 0)	// Only Use X > 0(else, Move to End Point)
	{
		a_OriginX = a_OriginX+a_OffsetX;
		a_OriginY = a_OriginY+a_OffsetY;
		a_OffsetX = -a_OffsetX;
		a_OffsetY = -a_OffsetY;
	}
	if(a_OffsetY < 0)
	{
		l_StepY = -l_StepY;
		a_OffsetY = -a_OffsetY;
	}

	l_Err = a_OffsetX >> 1;
	l_EndX = a_OriginX+a_OffsetX;

	for(; a_OriginX <= l_EndX; a_OriginX++)
	{
		if(l_SwapFlg == 0)
		{
			Ili9341_Point(a_Color, 1, a_OriginX, a_OriginY);
		}
		else
		{
			Ili9341_Point(a_Color, 1, a_OriginY, a_OriginX);
		}
		l_Err -= a_OffsetY;

		if(l_Err <	0)
		{
			a_OriginY += l_StepY;
			l_Err += a_OffsetX;
		}
	}
}

void Ili9341_LineEarc(uint16_t a_Color, int16_t a_Angle,
		int16_t a_OriginX, int16_t a_OriginY, int16_t a_OffsetX, int16_t a_OffsetY)
{
	int x0 = a_OriginX, x1 = a_OriginX+a_OffsetX, y0=a_OriginY, y1=a_OriginY+a_OffsetY;
	  int a = abs(x1-x0), b = abs(y1-y0), b1 = b&1; /* values of diameter */
	   long dx = 4*(1-a)*b*b, dy = 4*(b1+1)*a*a; /* error increment */
	   long err = dx+dy+b1*a*a, e2; /* error of 1.step */

	   if (x0 > x1)
	   {
		   x0 = x1;
		   x1 += a;
	   } /* if called with swapped points */
	   if (y0 > y1) y0 = y1; /* .. exchange them */
	   y0 += (b+1)/2;
	   y1 = y0-b1;   /* starting pixel */
	   a *= 8*a;
	   b1 = 8*b*b;

	   switch(a_Angle)
		{
			case 0 :
				do
				{
				   Ili9341_Point(a_Color, 1,x1, y0); /*   I. Quadrant */
				   e2 = 2*err;
				   if (e2 <= dy)
				   {
					   y0++; y1--;
					   err += dy += a;
				   }  /* y step */
				   if (e2 >= dx || 2*err > dy)
				   {
					   x0++;
					   x1--;
					   err += dx += b1;
				   } /* x step */
				}
				while (x0 <= x1);
				break;
			case 90 :
				do
				{
					Ili9341_Point(a_Color, 1,x0, y0); /*  II. Quadrant */
				   e2 = 2*err;
				   if (e2 <= dy)
				   {
					   y0++; y1--;
					   err += dy += a;
				   }  /* y step */
				   if (e2 >= dx || 2*err > dy)
				   {
					   x0++;
					   x1--;
					   err += dx += b1;
				   } /* x step */
				}
				while (x0 <= x1);
				break;
			case 180 :
				do
				{
				   Ili9341_Point(a_Color, 1,x0, y1); /* III. Quadrant */
				   e2 = 2*err;
				   if (e2 <= dy)
				   {
					   y0++; y1--;
					   err += dy += a;
				   }  /* y step */
				   if (e2 >= dx || 2*err > dy)
				   {
					   x0++;
					   x1--;
					   err += dx += b1;
				   } /* x step */
				}
				while (x0 <= x1);
				break;
			case 270 :
				do
				{
				   Ili9341_Point(a_Color, 1,x1, y1); /*  IV. Quadrant */
				   e2 = 2*err;
				   if (e2 <= dy)
				   {
					   y0++; y1--;
					   err += dy += a;
				   }  /* y step */
				   if (e2 >= dx || 2*err > dy)
				   {
					   x0++;
					   x1--;
					   err += dx += b1;
				   } /* x step */
				}
				while (x0 <= x1);
				break;
			default :
				break;
		}

	   while (y0-y1 < b)
	   {  /* too early stop of flat ellipses a=1 */
		   Ili9341_Point(a_Color, 1,x0-1, y0); /* -> finish tip of ellipse */
	       Ili9341_Point(a_Color, 1,x1+1, y0++);
	       Ili9341_Point(a_Color, 1,x0-1, y1);
	       Ili9341_Point(a_Color, 1,x1+1, y1--);
	   }
}

void Ili9341_HallowItri(uint16_t a_Color, int16_t a_Angle,
		int16_t a_OriginX, int16_t a_OriginY, int16_t a_OffsetX, int16_t a_OffsetY)
{
	if(a_OffsetX < 0)
	{
		a_OriginX += a_OffsetX;
		a_OffsetX = -a_OffsetX;
	}
	if(a_OffsetY < 0)
	{
		a_OriginY += a_OffsetY;
		a_OffsetY = -a_OffsetY;
	}

	switch(a_Angle)
	{
		case 0 :
			Ili9341_LineDiag(a_Color, a_OriginX, a_OriginY, 0, a_OffsetY);
			Ili9341_LineDiag(a_Color, a_OriginX, a_OriginY, a_OffsetX, a_OffsetY >> 1);
			Ili9341_LineDiag(a_Color, a_OriginX+a_OffsetX, a_OriginY+(a_OffsetY >> 1), -a_OffsetX, a_OffsetY >> 1);
			break;
		case 90 :
			Ili9341_LineDiag(a_Color, a_OriginX+(a_OffsetX >> 1), a_OriginY, -(a_OffsetX >> 1), a_OffsetY);
			Ili9341_LineDiag(a_Color, a_OriginX+(a_OffsetX >> 1), a_OriginY, a_OffsetX >> 1, a_OffsetY);
			Ili9341_LineDiag(a_Color, a_OriginX, a_OriginY+a_OffsetY, a_OffsetX, 0);
			break;
		case 180 :
			Ili9341_LineDiag(a_Color, a_OriginX+a_OffsetX, a_OriginY, -a_OffsetX, a_OffsetY >> 1);
			Ili9341_LineDiag(a_Color, a_OriginX, a_OriginY+(a_OffsetY >> 1), a_OffsetX, a_OffsetY >> 1);
			Ili9341_LineDiag(a_Color, a_OriginX+a_OffsetX, a_OriginY, 0, a_OffsetY);
			break;
		case 270 :
			Ili9341_LineDiag(a_Color, a_OriginX, a_OriginY, a_OffsetX, 0);
			Ili9341_LineDiag(a_Color, a_OriginX, a_OriginY, a_OffsetX >> 1, a_OffsetY);
			Ili9341_LineDiag(a_Color, a_OriginX+a_OffsetX, a_OriginY, -(a_OffsetX >> 1), a_OffsetY);
			break;
		default :
			break;
	}
}

void Ili9341_HallowAtri(uint16_t a_Color, int16_t a_Angle,
		int16_t a_OriginX, int16_t a_OriginY, int16_t a_OffsetX, int16_t a_OffsetY)
{
	if(a_OffsetX < 0)
	{
		a_OriginX += a_OffsetX;
		a_OffsetX = -a_OffsetX;
	}
	if(a_OffsetY < 0)
	{
		a_OriginY += a_OffsetY;
		a_OffsetY = -a_OffsetY;
	}

	switch(a_Angle)
	{
		case 0 :
			Ili9341_LineDiag(a_Color, a_OriginX, a_OriginY, 0, a_OffsetY);
			Ili9341_LineDiag(a_Color, a_OriginX+a_OffsetX, a_OriginY, -a_OffsetX, a_OffsetY);
			Ili9341_LineDiag(a_Color, a_OriginX, a_OriginY, a_OffsetX, 0);
			break;
		case 90 :
			Ili9341_LineDiag(a_Color, a_OriginX, a_OriginY, 0, a_OffsetY);
			Ili9341_LineDiag(a_Color, a_OriginX, a_OriginY, a_OffsetX, a_OffsetY);
			Ili9341_LineDiag(a_Color, a_OriginX, a_OriginY+a_OffsetY, a_OffsetX, 0);
			break;
		case 180 :
			Ili9341_LineDiag(a_Color, a_OriginX+a_OffsetX, a_OriginY, -a_OffsetX, a_OffsetY);
			Ili9341_LineDiag(a_Color, a_OriginX+a_OffsetX, a_OriginY, 0, a_OffsetY);
			Ili9341_LineDiag(a_Color, a_OriginX, a_OriginY+a_OffsetY, a_OffsetX, 0);
			break;
		case 270 :
			Ili9341_LineDiag(a_Color, a_OriginX, a_OriginY, a_OffsetX, a_OffsetY);
			Ili9341_LineDiag(a_Color, a_OriginX+a_OffsetX, a_OriginY, 0, a_OffsetY);
			Ili9341_LineDiag(a_Color, a_OriginX, a_OriginY, a_OffsetX, 0);
			break;
		default :
			break;
	}
}

void Ili9341_HallowRhom(uint16_t a_Color,
		int16_t a_OriginX, int16_t a_OriginY, int16_t a_OffsetX, int16_t a_OffsetY)
{
	Ili9341_LineDiag(a_Color, a_OriginX+(a_OffsetX >> 1), a_OriginY, -(a_OffsetX >> 1), a_OffsetY >> 1);
	Ili9341_LineDiag(a_Color, a_OriginX+(a_OffsetX >> 1), a_OriginY, a_OffsetX >> 1, a_OffsetY >> 1);
	Ili9341_LineDiag(a_Color, a_OriginX, a_OriginY+(a_OffsetY >> 1), a_OffsetX >> 1, a_OffsetY >> 1);
	Ili9341_LineDiag(a_Color, a_OriginX+a_OffsetX, a_OriginY+(a_OffsetY >> 1), -(a_OffsetX >> 1), a_OffsetY >> 1);
}

void Ili9341_HallowRect(uint16_t a_Color,
		int16_t a_OriginX, int16_t a_OriginY, int16_t a_OffsetX, int16_t a_OffsetY)
{
	Ili9341_LineDiag(a_Color, a_OriginX, a_OriginY, 0, a_OffsetY-1);
	Ili9341_LineDiag(a_Color, a_OriginX+a_OffsetX-1, a_OriginY, 0, a_OffsetY-1);
	Ili9341_LineDiag(a_Color, a_OriginX, a_OriginY, a_OffsetX-1, 0);
	Ili9341_LineDiag(a_Color, a_OriginX, a_OriginY+a_OffsetY-1, a_OffsetX-1, 0);
}

void Ili9341_HallowElli(uint16_t a_Color,
		int16_t a_OriginX, int16_t a_OriginY, int16_t a_OffsetX, int16_t a_OffsetY)
{
	Ili9341_HallowItri(a_Color, 0,	a_OriginX, a_OriginY, a_OffsetX, a_OffsetY);
	Ili9341_HallowItri(a_Color, 90,	a_OriginX, a_OriginY, a_OffsetX, a_OffsetY);
	Ili9341_HallowItri(a_Color, 180,	a_OriginX, a_OriginY, a_OffsetX, a_OffsetY);
	Ili9341_HallowItri(a_Color, 270,	a_OriginX, a_OriginY, a_OffsetX, a_OffsetY);
}

void Ili9341_FilledRect(uint16_t a_Color,
		int16_t a_OriginX, int16_t a_OriginY, int16_t a_OffsetX, int16_t a_OffsetY)
{
	uint8_t l_Upper = (uint8_t)(a_Color >> 8), l_Lower = (uint8_t)(a_Color & 0xff);
	volatile uint32_t l_HoriCnt = 0, l_VertCnt = 0;

//	Ili9341_LineDiag(a_Color, a_OriginX, a_OriginY, 0, a_OffsetY);
//	Ili9341_LineDiag(a_Color, a_OriginX+a_OffsetX, a_OriginY, 0, a_OffsetY);

	Ili9341_Cmd(0x2a);		// Command Column Address Set
	Ili9341_Data(a_OriginX >> 8);
	Ili9341_Data(a_OriginX & 0xff);
	Ili9341_Data((a_OriginX+a_OffsetX-1) >> 8);
	Ili9341_Data((a_OriginX+a_OffsetX-1) & 0xff);

	Ili9341_Cmd(0x2b);		// Command Page Address Set
	Ili9341_Data(a_OriginY >> 8);
	Ili9341_Data(a_OriginY & 0xff);
	Ili9341_Data((a_OriginY+a_OffsetY-1) >> 8);
	Ili9341_Data((a_OriginY+a_OffsetY-1) & 0xff);

	Ili9341_Cmd(0x2c);		// Command Memory Write
	LCD_SELECT;				// Enable Spi4 Chip Select
	LCD_DATA;				// Set Data(1) or Command(0)
	for(l_VertCnt = 0; l_VertCnt < a_OffsetY; l_VertCnt++)
	{
		for(l_HoriCnt = 0; l_HoriCnt < a_OffsetX; l_HoriCnt++)
		{
			LCD_TXBUFF(&l_Upper, 1);		// Transmit Spi4 Data
			LCD_TXBUFF(&l_Lower, 1);		// Transmit Spi4 Data
		}
	}
	LCD_UNSELECT;				// Disable Spi4 Chip Select
}

void Ili9341_Block_insert(	uint16_t a_Color,
							uint16_t u2t_column_sc, 		// Start Column
							uint16_t u2t_page_sp, 			// Start Page
							uint16_t u2t_column_ec, 		// End Column
							uint16_t u2t_page_ep,			// End Page
							uint16_t u2t_offset)
{
	uint8_t l_Upper = (uint8_t)(a_Color >> 8), l_Lower = (uint8_t)(a_Color & 0xff);
	volatile uint32_t l_HoriCnt = 0, l_VertCnt = 0;

	Ili9341_Cmd(0x2a);		// Command Column Address Set
	Ili9341_Data((u2t_column_sc + u2t_offset) >> 8);
	Ili9341_Data((u2t_column_sc + u2t_offset) & 0xff);
	Ili9341_Data((u2t_column_ec + u2t_offset) >> 8);
	Ili9341_Data((u2t_column_ec + u2t_offset) & 0xff);

	Ili9341_Cmd(0x2b);		// Command Page Address Set
	Ili9341_Data((u2t_page_sp + u2t_offset) >> 8);
	Ili9341_Data((u2t_page_sp + u2t_offset) & 0xff);
	Ili9341_Data((u2t_page_ep + u2t_offset) >> 8);
	Ili9341_Data((u2t_page_ep + u2t_offset) & 0xff);

	Ili9341_Cmd(0x2c);		// Command Memory Write
	LCD_SELECT;				// Enable Spi4 Chip Select
	LCD_DATA;				// Set Data(1) or Command(0)
	for(l_VertCnt = 0; l_VertCnt < (u2t_page_ep-u2t_page_sp) + 1; l_VertCnt++)
	{
		for(l_HoriCnt = 0; l_HoriCnt < (u2t_column_ec-u2t_column_sc) + 1; l_HoriCnt++)
		{
			LCD_TXBUFF(&l_Upper, 1);		// Transmit Spi4 Data
			LCD_TXBUFF(&l_Lower, 1);		// Transmit Spi4 Data
			while(LCD_BUSY == 1);			// Wait for Terminate of Transfer
		}
	}
	LCD_UNSELECT;				// Disable Spi4 Chip Select
}
